package edu.jenks.dist.barrons.matrix;

/**
 * @author Ted Jenks
 *
 */
public abstract class AbstractArrayUtil {

	/**
	 * <p>Reverses elements of array <code>arr</code>.</p>
	 * <b>Precondition:</b> <code>arr.length &gt; 0</code><br>
	 * <b>Postcondition:</b> The elements of <code>arr</code> have been reversed.
	 * @param arr the array to manipulate
	 */
	public abstract void reverseArray(int[] arr);
}
