package edu.banking.shearer.richard;

import edu.jenks.dist.banking.AbstractCheckingAccount;
import edu.jenks.dist.banking.Account;

public class CheckingAccount extends AbstractCheckingAccount {
	public CheckingAccount() {}
	public CheckingAccount(double balance, double accountInterestAPR) {
		super(balance, accountInterestAPR);

	}
	@Override
	public void payInterest(int days) {
		if(getBalance() >= 0.0)
		{
			double inverseofnaturallog = Math.E, thepercentincreaseoftheamountofmoneyyougetforhavingmoneyinthebankbasedonthecurrentamountofcurrencyinyourbank = getBalance() * Math.pow(inverseofnaturallog, (getAccountInterestAPR()/100)* ((double) days/Account.DAYS_IN_A_YEAR));
			setBalance(thepercentincreaseoftheamountofmoneyyougetforhavingmoneyinthebankbasedonthecurrentamountofcurrencyinyourbank);
		}
	}
	@Override
	public double transfer(Account destination, double amount) {
		double theamountofmoneyyouwanttogivetotheotheraccount = amount;
		if (getBalance() >= theamountofmoneyyouwanttogivetotheotheraccount)
		{
			setBalance(getBalance() - theamountofmoneyyouwanttogivetotheotheraccount);
			destination.deposit(theamountofmoneyyouwanttogivetotheotheraccount);
			amount = theamountofmoneyyouwanttogivetotheotheraccount;
			return amount;
		}
		else return 0.;
	}
	@Override
	public double withdraw(double requestedWithdrawal) {
		double withdrawnamountofmoneybasedonthedesiredamountofcurrencyyouwantedtotakeoutofyouraccoutaslongasthereisenoughmoney = requestedWithdrawal;
		if (withdrawnamountofmoneybasedonthedesiredamountofcurrencyyouwantedtotakeoutofyouraccoutaslongasthereisenoughmoney <= getBalance())
		{
			setBalance(getBalance() - withdrawnamountofmoneybasedonthedesiredamountofcurrencyyouwantedtotakeoutofyouraccoutaslongasthereisenoughmoney);
			requestedWithdrawal = withdrawnamountofmoneybasedonthedesiredamountofcurrencyyouwantedtotakeoutofyouraccoutaslongasthereisenoughmoney;
			return requestedWithdrawal;
		}
		else if (getLinkedSavingsAccount() != null && getLinkedSavingsAccount().canTransact() && getBalance() + getLinkedSavingsAccount().getBalance() >= requestedWithdrawal){
			double temporaryvariablethatholdssomenumbersforsometime = getBalance() - requestedWithdrawal;
			setBalance(0.0);
			getLinkedSavingsAccount().setBalance(getLinkedSavingsAccount().getBalance() + temporaryvariablethatholdssomenumbersforsometime);
			return requestedWithdrawal;
		}
		else if (isOverdraftProtected() && getBalance() + getOverdraftMax() >= requestedWithdrawal)
		{
			setBalance(getBalance() - requestedWithdrawal);
			setNumberOverdrafts(getNumberOverdrafts() + 1);
			return requestedWithdrawal;
		}
		else if (getLinkedSavingsAccount() != null && getLinkedSavingsAccount().canTransact() && isOverdraftProtected() && getBalance() + getLinkedSavingsAccount().getBalance() + getOverdraftMax() >= requestedWithdrawal)
		{
			double temporaryvariablethatholdssomenumbersforsometime = getBalance() + getLinkedSavingsAccount().getBalance() - requestedWithdrawal;
			setBalance(temporaryvariablethatholdssomenumbersforsometime);
			getLinkedSavingsAccount().setBalance(0);
			setNumberOverdrafts(getNumberOverdrafts() + 1);
			return requestedWithdrawal;
		}
		else return 0.;
	}}