package edu.banking.shearer.richard;

import edu.jenks.dist.banking.AbstractSavingsAccount;
import edu.jenks.dist.banking.Account;

public class SavingsAccount extends AbstractSavingsAccount {

	public SavingsAccount() {}
	public SavingsAccount(double balance, double accountInterestAPR) {
		super(balance, accountInterestAPR);
	}
	@Override
	public boolean canTransact() {
		return (getNumTransactions() < getMaxMonthlyTransactions());
	}
	@Override
	public void payInterest(int days) {
		double theannualpercentagerateoftheaccount = getAccountInterestAPR()/100;
		if(getBalance() >= 0.0)
		{
			double inverseofnaturallog = Math.E, thepercentincreaseoftheamountofmoneyyougetforhavingmoneyinthebankbasedonthecurrentamountofcurrencyinyourbank = getBalance() * Math.pow(inverseofnaturallog, (theannualpercentagerateoftheaccount* ((double) days/Account.DAYS_IN_A_YEAR)));
			setBalance(thepercentincreaseoftheamountofmoneyyougetforhavingmoneyinthebankbasedonthecurrentamountofcurrencyinyourbank);
		}
	}
	@Override
	public double withdraw(double requestedWithdrawal) {
		double withdrawnamountofmoneybasedonthedesiredamountofcurrencyyouwantedtotakeoutofyouraccoutaslongasthereisenoughmoney = requestedWithdrawal;
		int thenumberoftransactionsthisaccounthasdoneinthepast = getNumTransactions();
		if (canTransact()){
			if (withdrawnamountofmoneybasedonthedesiredamountofcurrencyyouwantedtotakeoutofyouraccoutaslongasthereisenoughmoney <= getBalance())
			{
				setBalance(getBalance() - withdrawnamountofmoneybasedonthedesiredamountofcurrencyyouwantedtotakeoutofyouraccoutaslongasthereisenoughmoney);
				requestedWithdrawal = withdrawnamountofmoneybasedonthedesiredamountofcurrencyyouwantedtotakeoutofyouraccoutaslongasthereisenoughmoney;
				setNumTransactions(thenumberoftransactionsthisaccounthasdoneinthepast + 1);
				return requestedWithdrawal;
			}
			else return 0.;
		}
		else return 0.;
	}
	@Override
	public double transfer(Account destination, double amount) {
		if (getBalance() >= amount)
		{
			setBalance(getBalance() - amount);
			destination.deposit(amount);
			setNumTransactions(getNumTransactions() + 1);
			return (amount);
		}
		else return 0.;
	}}